mapper.select:
select * from(
select * from (
SELECT evid, crid,mid,
crtitle,crregdate,crvote, rownum rn FROM (
SELECT evid, crid,mid,
crtitle,crregdate,crvote
FROM crecipe


		and evid = #{evid}


order by crregdate DESC
)
)

where rownum <= #{pageNum} * #{amount}

)

where rn > (#{pageNum}-1) * #{amount}

SELECT A.evid, A.crid, A.count, B.mid, B.crtitle, B.crregdate, B.crvote FROM (SELECT evid, crid, COUNT(*) AS count FROM event_vote
evid = #{evid}
GROUP BY evid, crid ORDER BY count DESC) A JOIN (SELECT evid, crid,mid, crtitle,crregdate,crvote FROM crecipe
crvote=1
and evid = #{evid}
ORDER BY evid DESC, crregdate DESC) B ON A.evid = B.evid AND A.crid = B.crid

SELECT count(*) FROM (
SELECT *
FROM crecipe



		and evid = #{evid}


)

mapper.update:
update crecipe
set crvote
=#{crvote}
where crid = #{crid}
