mapper.insert:

BEGIN
    INSERT INTO cart_share (
        csno,
        cskey,
        mid
    )VALUES(
        cs_seq.NEXTVAL,
        #{cskey},
        #{mid}
    );

    UPDATE members 
    SET csno = (SELECT csno FROM cart_share WHERE mid= #{mid})
    WHERE mid = #{mid};
END;

mapper.update:
UPDATE members 
SET csno = (SELECT csno FROM cart_share WHERE csno = #{csno} AND cskey = #{cskey}) 
WHERE mid = #{mid}
